mapper.select:
SELECT record_id,data_time,count(data_count) as data_count,area_id,today.dept_id,data_desc FROM ts_today_onwork_count today left join tb_pf_department dept on today.dept_id=dept.dept_id


     and dept.parent_id=#{deptPid}


    and today.dept_id=#{deptId}


    and today.area_id=#{areaId}


GROUP BY data_time

select type.no as id,dept_name,type.name,count(user_id) as count from v_attend_onwork_type type left join ( SELECT u.user_id, u.dept_id, u.user_name, t.pass_datetime, dept.dept_name, t.device_direction, (case when device_direction=1 then 1 when device_direction=2 && pass_datetime >=date_sub(now(), INTERVAL 1 HOUR) then 2 else 3 end) as id FROM tb_user u LEFT JOIN tb_pf_department dept on u.dept_id=dept.dept_id LEFT OUTER JOIN ( SELECT user_id, pass_datetime, device_direction FROM view_today_pass_last ) t ON u.user_id = t.user_id
and u.dept_parent_id = #{deptPid}
and u.dept_id=#{deptId}
) r on type.no=r.id GROUP BY type.no

select area.area_id as id, area.area_name as `name`,sum(today.value) AS count  from tb_pf_area area left join view_today_onwork_count_by_area today on area.area_id=today.id
LEFT JOIN tb_pf_department dept on today.dept_id=dept.dept_id

  area.area_id>1

    and dept.parent_id = #{deptPid}


    and dept_id=#{deptId}


GROUP BY area.area_id

SELECT dept.dept_id,dept.dept_name as name, sum(value) AS count
FROM view_today_onwork_count_by_area area LEFT JOIN tb_pf_department dept on area.dept_id=dept.dept_id


    and dept.parent_id = #{deptPid}


    and area.id=#{areaId}


GROUP BY dept.dept_id

SELECT pass_datetime AS last_passtime, u.user_id, u.dept_id, dept.dept_name, u.user_name, (case when device_direction=1 then '进' when device_direction=2 then '出' else '' end) as lastDirect, device_area_id AS area_id, area.area_name as area_name, (case when device_direction=1 then '在岗' when oa.leave_name is not null then oa.leave_name else '不在岗' end) as onwork_status, u.identification_pic_id FROM tb_user u LEFT JOIN view_today_pass_last pass ON u.user_id = pass.user_id LEFT JOIN tb_pf_area area ON pass.device_area_id = area.area_id LEFT OUTER JOIN tb_pf_department dept ON u.dept_id = dept.dept_id left outer join (select leaveuser.userName, leavetype.name as leave_name from tb_third_oa_leaveuser leaveuser, tb_third_oa_leavetype leavetype where leaveuser.leaveType=leavetype.id and now() BETWEEN leaveuser.beginTime and if(leaveuser.cancelLeaveTime is not null,if(leaveuser.endTime>leaveuser.cancelLeaveTime,leaveuser.cancelLeaveTime, leaveuser.cancelLeaveTime),leaveuser.endTime)) oa on pass.user_name=oa.userName
and u.dept_id=#{deptId}
and dept.parent_id=#{deptPid}
order by pass.device_direction desc

select t.last_passtime,t.lastDirect , t.user_id, t.user_name, t.dept_id,t.dept_name,t.area_id,t.area_name, t.onwork_status, u.identification_pic_id from ( SELECT pass_datetime AS last_passtime, user_id, pass.dept_id, dept.dept_name, user_name, '进' as lastDirect, device_area_id AS area_id, area.area_name as area_name, '在岗' AS onwork_status FROM view_today_pass_last pass left join tb_pf_area area on pass.device_area_id=area.area_id left outer join tb_pf_department dept on pass.dept_id=dept.dept_id where pass.device_direction=1
and pass.dept_id=#{deptId}
and dept.parent_id=#{deptPid}
) t left join tb_user u on t.user_id=u.user_id

select t.last_passtime,t.lastDirect , t.user_id, t.user_name, t.dept_id,t.dept_name,t.area_id,t.area_name, t.onwork_status, u.identification_pic_id from ( SELECT pass_datetime AS last_passtime, user_id, pass.dept_id, dept.dept_name, user_name, '出' as lastDirect, device_area_id AS area_id, area.area_name as area_name, '临时离岗' AS onwork_status FROM view_today_pass_last pass left join tb_pf_area area on pass.device_area_id=area.area_id left outer join tb_pf_department dept on pass.dept_id=dept.dept_id where pass.device_direction=2 and pass.pass_datetime >= DATE_SUB(now(),INTERVAL 1 HOUR)
and pass.dept_id=#{deptId}
and dept.parent_id=#{deptPid}
) t left join tb_user u on t.user_id=u.user_id

select t.last_passtime,t.lastDirect , t.user_id, t.user_name, t.dept_id,t.dept_name,t.area_id,t.area_name, t.onwork_status, u.identification_pic_id from ( SELECT pass_datetime AS last_passtime, user_id, pass.dept_id, dept.dept_name, user_name, '出' as lastDirect, device_area_id AS area_id, area.area_name as area_name, (case when oa.leave_name is not null then oa.leave_name else '不在岗' end) as onwork_status FROM view_today_pass_last pass left join tb_pf_area area on pass.device_area_id=area.area_id left outer join tb_pf_department dept on pass.dept_id=dept.dept_id left outer join (select leaveuser.userName, leavetype.name as leave_name from tb_third_oa_leaveuser leaveuser, tb_third_oa_leavetype leavetype where leaveuser.leaveType=leavetype.id and now() BETWEEN leaveuser.beginTime and if(leaveuser.cancelLeaveTime is not null,if(leaveuser.endTime>leaveuser.cancelLeaveTime,leaveuser.cancelLeaveTime, leaveuser.cancelLeaveTime),leaveuser.endTime)) oa on pass.user_name=oa.userName where pass.device_direction=2 and pass.pass_datetime < DATE_SUB(now(),INTERVAL 1 HOUR)
and pass.dept_id=#{deptId}
and dept.parent_id=#{deptPid}
) t left join tb_user u on t.user_id=u.user_id
